/**
 * Sage Coast Realty Dynamic Gallery Script
 * 
 * This snippet adds a dynamic gallery script to all pages.
 * It will automatically initialize any element with class "main-image"
 * and add navigation functionality.
 */

add_action('wp_footer', 'sage_coast_gallery_script');

function sage_coast_gallery_script() {
    ?>
    <script>
    // Sage Coast Realty Dynamic Gallery Module
    (function() {
        'use strict';
        
        // Gallery class for multiple instances
        class PropertyGallery {
            constructor(container) {
                this.container = container;
                this.images = [];
                this.currentIndex = 0;
                this.init();
            }
            
            init() {
                // Get images from data attribute or thumbnails
                this.collectImages();
                
                if (this.images.length === 0) {
                    console.error('No images found for gallery');
                    return;
                }
                
                // Create navigation
                this.createNavigation();
                
                // Set initial image
                this.setMainImage(0);
                
                // Set up event listeners
                this.setupEventListeners();
                
                // Preload images
                this.preloadImages();
            }
            
            collectImages() {
                // Method 1: Check for data-images attribute
                const dataImages = this.container.dataset.images;
                if (dataImages) {
                    try {
                        this.images = JSON.parse(dataImages);
                        return;
                    } catch (e) {
                        console.error('Invalid JSON in data-images attribute');
                    }
                }
                
                // Method 2: Check for data-image-1, data-image-2, etc.
                let index = 1;
                while (this.container.dataset[`image${index}`]) {
                    this.images.push(this.container.dataset[`image${index}`]);
                    index++;
                }
                
                // Method 3: Collect from thumbnails
                if (this.images.length === 0) {
                    const thumbnails = this.container.parentElement.querySelectorAll('.thumbnail[data-image]');
                    thumbnails.forEach(thumb => {
                        if (thumb.dataset.image) {
                            this.images.push(thumb.dataset.image);
                        }
                    });
                }
                
                // Method 4: Get from thumbnail background images
                if (this.images.length === 0) {
                    const thumbnails = this.container.parentElement.querySelectorAll('.thumbnail');
                    thumbnails.forEach(thumb => {
                        const bgImage = window.getComputedStyle(thumb).backgroundImage;
                        if (bgImage && bgImage !== 'none') {
                            const url = bgImage.match(/url\(["']?(.+?)["']?\)/);
                            if (url && url[1]) {
                                this.images.push(url[1]);
                            }
                        }
                    });
                }
                
                // Add main image if it has a background
                const mainBg = window.getComputedStyle(this.container).backgroundImage;
                if (mainBg && mainBg !== 'none') {
                    const url = mainBg.match(/url\(["']?(.+?)["']?\)/);
                    if (url && url[1] && !this.images.includes(url[1])) {
                        this.images.unshift(url[1]); // Add to beginning
                    }
                }
            }
            
            createNavigation() {
                // Check if navigation already exists
                if (this.container.querySelector('.gallery-nav-wrapper')) {
                    return;
                }
                
                // Create navigation wrapper
                const navWrapper = document.createElement('div');
                navWrapper.className = 'gallery-nav-wrapper';
                navWrapper.style.cssText = `
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    pointer-events: none;
                    z-index: 100;
                `;
                
                // Create buttons
                const prevButton = this.createButton('prev', '❮');
                const nextButton = this.createButton('next', '❯');
                
                navWrapper.appendChild(prevButton);
                navWrapper.appendChild(nextButton);
                this.container.appendChild(navWrapper);
            }
            
            createButton(type, symbol) {
                const button = document.createElement('button');
                button.className = `nav-arrow ${type}`;
                button.innerHTML = symbol;
                button.setAttribute('aria-label', `${type === 'prev' ? 'Previous' : 'Next'} image`);
                
                const styles = `
                    position: absolute;
                    ${type === 'prev' ? 'left: 20px' : 'right: 20px'};
                    top: 50%;
                    transform: translateY(-50%);
                    background: rgba(255, 255, 255, 0.9);
                    color: #1f2937;
                    border: 2px solid #1f2937;
                    width: 50px;
                    height: 50px;
                    border-radius: 50%;
                    font-size: 24px;
                    font-weight: bold;
                    cursor: pointer;
                    z-index: 101;
                    pointer-events: all;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    transition: all 0.3s ease;
                    box-shadow: 0 2px 8px rgba(0,0,0,0.3);
                    line-height: 1;
                    padding: 0;
                `;
                
                button.style.cssText = styles;
                
                // Hover effects
                button.addEventListener('mouseenter', function() {
                    this.style.background = '#fff';
                    this.style.color = '#d4af37';
                    this.style.borderColor = '#d4af37';
                    this.style.transform = 'translateY(-50%) scale(1.1)';
                });
                
                button.addEventListener('mouseleave', function() {
                    this.style.background = 'rgba(255, 255, 255, 0.9)';
                    this.style.color = '#1f2937';
                    this.style.borderColor = '#1f2937';
                    this.style.transform = 'translateY(-50%)';
                });
                
                return button;
            }
            
            setMainImage(index) {
                this.container.style.backgroundImage = `url('${this.images[index]}')`;
                this.container.style.backgroundSize = 'cover';
                this.container.style.backgroundPosition = 'center';
                
                // Update active thumbnail
                const galleryWrapper = this.container.closest('.gallery');
                if (galleryWrapper) {
                    const thumbnails = galleryWrapper.querySelectorAll('.thumbnail');
                    thumbnails.forEach((thumb, i) => {
                        if (i === index) {
                            thumb.style.border = '3px solid #d4af37';
                            thumb.style.transform = 'scale(1.05)';
                        } else {
                            thumb.style.border = '3px solid transparent';
                            thumb.style.transform = 'scale(1)';
                        }
                    });
                }
                
                // Update counter if it exists
                const counter = this.container.querySelector('.image-counter');
                if (counter) {
                    counter.textContent = `${index + 1} / ${this.images.length}`;
                }
            }
            
            setupEventListeners() {
                // Navigation clicks
                this.container.addEventListener('click', (e) => {
                    if (e.target.classList.contains('nav-arrow')) {
                        if (e.target.classList.contains('prev')) {
                            this.currentIndex = (this.currentIndex - 1 + this.images.length) % this.images.length;
                        } else if (e.target.classList.contains('next')) {
                            this.currentIndex = (this.currentIndex + 1) % this.images.length;
                        }
                        this.setMainImage(this.currentIndex);
                    }
                });
                
                // Thumbnail clicks
                const galleryWrapper = this.container.closest('.gallery');
                if (galleryWrapper) {
                    const thumbnails = galleryWrapper.querySelectorAll('.thumbnail');
                    thumbnails.forEach((thumb, index) => {
                        thumb.addEventListener('click', () => {
                            this.currentIndex = index;
                            this.setMainImage(this.currentIndex);
                        });
                    });
                }
                
                // Touch/swipe support
                let touchStartX = 0;
                let touchEndX = 0;
                
                this.container.addEventListener('touchstart', (e) => {
                    touchStartX = e.changedTouches[0].screenX;
                }, false);
                
                this.container.addEventListener('touchend', (e) => {
                    touchEndX = e.changedTouches[0].screenX;
                    this.handleSwipe();
                }, false);
                
                const handleSwipe = () => {
                    if (touchEndX < touchStartX - 50) {
                        // Swipe left - next image
                        this.currentIndex = (this.currentIndex + 1) % this.images.length;
                        this.setMainImage(this.currentIndex);
                    }
                    if (touchEndX > touchStartX + 50) {
                        // Swipe right - previous image
                        this.currentIndex = (this.currentIndex - 1 + this.images.length) % this.images.length;
                        this.setMainImage(this.currentIndex);
                    }
                };
                
                this.handleSwipe = handleSwipe;
            }
            
            preloadImages() {
                this.images.forEach(src => {
                    const img = new Image();
                    img.src = src;
                });
            }
        }
        
        // Initialize all galleries on the page
        function initializeGalleries() {
            const galleries = document.querySelectorAll('.main-image');
            galleries.forEach(gallery => {
                if (!gallery.hasAttribute('data-gallery-initialized')) {
                    new PropertyGallery(gallery);
                    gallery.setAttribute('data-gallery-initialized', 'true');
                }
            });
            
            // Apply dark backgrounds if wrapper exists
            const wrappers = document.querySelectorAll('.property-listing-wrapper');
            wrappers.forEach(wrapper => {
                wrapper.style.backgroundColor = '#0b131e';
            });
        }
        
        // Initialize on various load events
        if (document.readyState === 'loading') {
            document.addEventListener('DOMContentLoaded', initializeGalleries);
        } else {
            initializeGalleries();
        }
        
        // Reinitialize after potential AJAX loads
        setTimeout(initializeGalleries, 1000);
        window.addEventListener('load', () => setTimeout(initializeGalleries, 100));
        
        // Make available globally for manual initialization
        window.PropertyGallery = PropertyGallery;
        window.initializeGalleries = initializeGalleries;
    })();
    </script>
    <?php
}

